public class largest_of_two {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		int b =20;
		if(a>b)
		{
			System.out.println(a+" is largest than "+b);
		}
		else
		{
			System.out.println(b+" is largest than "+a);
		}
	}

}
