package Wrapper_classes;

public class maths_functions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double s = 453;
		int a = -10;
		int b = 20;
		int angle = 45;
		float h = 6.7897f;
		System.out.println("Square root of "+s+" = "+Math.sqrt(s));
		System.out.println("Cube root of "+s+" = "+Math.cbrt(s));
		System.out.println("Absolute value of "+a+" = "+Math.abs(a));
		System.out.println("pow of "+s+" with respect to 2 = "+Math.pow(s,2));
		System.out.println("first no = 10 .second number = 20, max of 10 & 20 = "+Math.max(10,20));
		System.out.println("first no = 10 .second number = 20, min of 10 & 20 = "+Math.min(10,20));
		System.out.println("log of "+b+" = "+Math.log(b));
		System.out.println("log to the base of 10 of number  "+b+" = "+Math.log10(b));
		System.out.println("Exponent of "+b+" = "+Math.exp(b));
		System.out.println("sin of "+angle+" = "+Math.sin(angle));
		System.out.println("cos of "+angle+" = "+Math.cos(angle));
		System.out.println("tan of "+angle+" = "+Math.tan(angle));
		System.out.println("asin of "+angle+" = "+Math.asin(angle));
		System.out.println("acos "+angle+" = "+Math.acos(angle));
		System.out.println("atan "+angle+" = "+Math.atan(angle));
		System.out.println("to Degree value given in radians "+" = "+Math.toDegrees(3.56));
		System.out.println("to Randians value given in degree "+angle+" = "+Math.toRadians(angle));
		System.out.println("Ceil of "+h+" = "+Math.ceil(h));
		System.out.println("Floor of "+h+" = "+Math.floor(h));
		System.out.println("Round of "+h+" = "+Math.round(h));
		System.out.println("Random Number = "+Math.random());
		
	}

}
